String imagem, titulo, subtitulo, texto = "";

class Informacao extends StatefulWidget {
  Informacao(String imagemt, String titulot, String subtitulot, String textot) {
    imagem = imagemt;
    titulo = titulot;
    subtitulo = subtitulot;
    texto = textot;
  }
  @override
  _InformacaoState createState() => _InformacaoState();
}

class _InformacaoState extends State<Informacao> {
  bool start = true;
  Duration animacao = Duration(milliseconds: 200);
  void initState() {
    super.initState();
    WidgetsBinding.instance.addPostFrameCallback((_) => animacaoinicial());
  }

  animacaoinicial() {
    Future.delayed(Duration(milliseconds: 100)).then((_) {
      setState(() {
        start = false;
      });
    });
  }

  Widget build(BuildContext context) {
    var appBar = AppBar();
    double screenheightsize =
        MediaQuery.of(context).size.height - appBar.preferredSize.height;
    bool verif = exibiralerta();

    return verif == true
        ? Scaffold(
            appBar: AppBar(
              title: Text('$titulo',
                  style: GoogleFonts.poppins(
                    letterSpacing: 0,
                    fontSize: 20,
                    color: Colors.white,
                    fontWeight: FontWeight.bold,
                  )),
              backgroundColor: corprincipal,
            ),
            body: ListView(children: <Widget>[
              Container(
                height: screenheightsize * .3,
                child: Center(
                  child: Image.network(
                      'http://www.anorosa.com.br/storage/$imagem'),
                ),
              ),
              AnimatedContainer(
                duration: animacao,
                transform: start ? _stateBoxStart() : Matrix4.identity()
                  ..translate(0.0, 0.0),
                width: MediaQuery.of(context).size.width,

                //margin: EdgeInsets.only(top: 5, left: 5),
                decoration: BoxDecoration(color: Colors.white, boxShadow: [
                  BoxShadow(
                      color: Colors.grey[600],
                      blurRadius: 3.0,
                      spreadRadius: 0.0, //aumenta a sombra
                      offset: Offset(
                        0.0, // Horizontal
                        -5.0, // Vertical
                      ))
                ]),
                child: Column(
                  children: <Widget>[
                    Container(
                      padding: EdgeInsets.only(
                        right: MediaQuery.of(context).size.width * 0.1,
                        left: MediaQuery.of(context).size.width * 0.1,
                      ),
                      child: ListTile(
                        title: Text(
                          '$titulo',
                          style: GoogleFonts.poppins(
                            letterSpacing: 1,
                            fontSize: 20,
                            color: Colors.black,
                            fontWeight: FontWeight.bold,
                          ),
                          textAlign: TextAlign.center,
                        ),
                        subtitle: Text(
                          '$subtitulo',
                          style: GoogleFonts.poppins(
                            letterSpacing: 1,
                            fontSize: 15,
                            color: Colors.grey,
                            //fontWeight: FontWeight.bold,
                          ),
                          textAlign: TextAlign.right,
                        ),
                      ),
                    ),
                    Container(
                      padding: EdgeInsets.only(
                        right: MediaQuery.of(context).size.width * 0.05,
                        left: MediaQuery.of(context).size.width * 0.05,
                      ),
                      child: Text(
                        '        $texto',
                        style: GoogleFonts.poppins(
                          letterSpacing: 1,
                          fontSize: 15,
                          //color: Colors.grey,
                          fontWeight: FontWeight.w400,
                        ),
                        textAlign: TextAlign.justify,
                      ),
                      margin: EdgeInsets.all(10),
                    ),
                    Container(height: screenheightsize * 0.1),
                  ],
                ),
              )
            ]),
            backgroundColor: Colors.white)
        : Scaffold(
            appBar: AppBar(
              title: const Text('Formulário'),
              backgroundColor: corprincipal,
            ),
          );
  }

  _stateBoxStart() {
    return Matrix4.identity()
      ..translate(0.0, MediaQuery.of(context).size.height);
  }
}

exibiralerta() {
  bool verif = true;
  if (imagem == null || titulo == null || subtitulo == null || texto == null) {
    verif = false;
    return verif;
  }
  return verif;
}

showAlertDialog1(BuildContext context) {
  // configura o button
  Widget okButton = FlatButton(
    child: Text("OK"),
    onPressed: () {
      Navigator.pop(context);
    },
  );
  // configura o  AlertDialog
  AlertDialog alerta = AlertDialog(
    title: Text("Houve um erro ao carregar a informação"),
    content: Text("Você será redirecionado a tela anterior."),
    actions: [
      okButton,
    ],
  );
  // exibe o dialog
  showDialog(
    context: context,
    builder: (BuildContext context) {
      return alerta;
    },
  );
}
